// LoginSignUp.js
import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const LoginSignUp = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('dealer');
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');

  // Additional signup fields
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [idProof, setIdProof] = useState('');

  const handleToggle = () => {
    setError('');
    setIsLogin((prev) => !prev);
  };

  const handleRoleToggle = () => {
    setRole((prev) => (prev === 'Dealer' ? 'Farmer' : 'dealer'));
  };

  const handleAuth = async () => {
    try {
      const userData = { email, password };

      if (!isLogin) {
        // Additional fields for signup
        userData.Name = name;
        userData.Date_of_Birth = dob;
        userData.Id_Proof = idProof;
      }

      if (isLogin) {
        // Login
        const response = await axios.post(`http://localhost:5000/${role}/login`, userData);
        const token = response.data.token;
        console.log('Login successful! Token:', token);
        // Save the token in localStorage or state for future requests
      } else {
        // Signup
        await axios.post(`http://localhost:5000/${role}/signup`, userData);
        console.log('Signup successful!');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred');
    }
  };

  return (
    <div className="container-fluid login-signup-container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header text-center bg-primary">
              <h2 className="text-light">{isLogin ? 'Login' : 'Sign Up'}</h2>
              <p className="text-light">Role: {role}</p>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleRoleToggle}
              >
                Toggle Role
              </button>
            </div>
            <div className="card-body">
              <form>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    Email:
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">
                    Password:
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>

                {!isLogin && (
                  <>
                    <div className="mb-3">
                      <label htmlFor="name" className="form-label">
                        Name:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                      />
                    </div>

                    <div className="mb-3">
                      <label htmlFor="dob" className="form-label">
                        Date of Birth:
                      </label>
                      <input
                        type="date"
                        className="form-control"
                        id="dob"
                        value={dob}
                        onChange={(e) => setDob(e.target.value)}
                      />
                    </div>

                    <div className="mb-3">
                      <label htmlFor="idProof" className="form-label">
                        ID Proof:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="idProof"
                        value={idProof}
                        onChange={(e) => setIdProof(e.target.value)}
                      />
                    </div>
                  </>
                )}

                {error && <div className="alert alert-danger">{error}</div>}

                <button
                  type="button"
                  className="btn btn-success"
                  onClick={handleAuth}
                >
                  {isLogin ? 'Login' : 'Sign Up'}
                </button>

                <p className="mt-3">
                  {isLogin
                    ? "Don't have an account? "
                    : 'Already have an account? '}
                  <button
                    type="button"
                    className="link-button"
                    onClick={handleToggle}
                  >
                    {isLogin ? 'Sign Up' : 'Login'}
                  </button>
                </p>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignUp;
