// LoginSignUp.js
import React, { useState } from 'react';
import axios from 'axios';

const LoginSignUp = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('dealer');
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState(null);

  const handleToggle = () => {
    setError(null);
    setIsLogin((prev) => !prev);
  };

  const handleRoleToggle = () => {
    setRole((prev) => (prev === 'dealer' ? 'farmer' : 'dealer'));
  };

  const handleAuth = async () => {
    try {
      if (isLogin) {
        // Login
        const response = await axios.post('/${role}`/login', { email, password });
        const token = response.data.token;
        console.log('Login successful! Token:', token);
        // Save the token in localStorage or state for future requests
      } else {
        // Signup
        await axios.post('/api/signup', { email, password, role });
        console.log('Signup successful!');
      }
    } catch (err) {
      setError(err.response.data.error || 'An error occurred');
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
              <p>Role: {role}</p>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleRoleToggle}
              >
                Toggle Role
              </button>
            </div>
            <div className="card-body">
              <form>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    Email:
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="password" className="form-label">
                    Password:
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>

                {error && <div className="alert alert-danger">{error}</div>}

                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={handleAuth}
                >
                  {isLogin ? 'Login' : 'Sign Up'}
                </button>

                <p className="mt-3">
                  {isLogin
                    ? "Don't have an account? "
                    : 'Already have an account? '}
                  <button type="button" className="link-button" onClick={handleToggle}>
                    {isLogin ? 'Sign Up' : 'Login'}
                  </button>
                </p>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignUp;
